@main def hello: Unit = 
  println("Welcome to day two of the Advent of Code!")
  val result = getThePositionResult(input)
  println("The value of the position is: " + result)
  val result2 = getThePositionResult2(input)
  println("The second calculation of the position is: " + result2)


def getDirectionValue(input: List[Tuple2[String, Int]], directionName: String): Int = input
      .filter(_._1 == directionName)
      .map(_._2)
      .foldLeft(0)(_+_)

def getThePositionResult(input: List[Tuple2[String, Int]]) :Int = 
  val xAxisValue = getDirectionValue(input, "forward")
  val zAxisPositiveValue = getDirectionValue(input, "down")
  val zAxisNegativeValue = getDirectionValue(input, "up")
  xAxisValue * (zAxisPositiveValue - zAxisNegativeValue)

///////


def getThePositionResult2(input: List[Tuple2[String, Int]]): Int = {
  var position = (0, 0, 0)
  for (newDirection <- input) {
    position = calculateNewPosition(position, newDirection)
  }
  position._1 * position._3
} 

//position(horizontalPosition, aim, depth)
def calculateNewPosition(position:Tuple3[Int, Int, Int], elementToBeParsed:Tuple2[String, Int]): Tuple3[Int, Int, Int] =
  var horizontalPosition = position._1
  var aim = position._2
  var depth = position._3
  elementToBeParsed match {
    case ("forward", value:Int) => {
        horizontalPosition += value
        depth += aim * value }
    case ("up", value:Int) => aim -= value
    case ("down", value:Int) => aim += value
    case _ =>
  }
  return (horizontalPosition, aim, depth)



